mapper.insert:

    SELECT mymachine_seq.NEXTVAL FROM DUAL

INSERT INTO mymachine (myMachineNo, custNo, gymNo, machineNo, myMachineStatus)
SELECT #{myMachineNo}, #{custNo}, #{gymNo}, #{machineNo}, '1' FROM DUAL
WHERE NOT EXISTS (
SELECT 1 FROM mymachine
WHERE custNo = #{custNo}
  AND machineNo = #{machineNo}
)
